sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
var myState = g_form.getValue('survey_state');
var myOwnerValue = g_form.getValue('owner_approval');
if(myState =="Not Started"){
	g_form.setValue('owner_approval',"Not Yet Completed");
	g_form.setReadOnly('owner_approval',true);
}
if(myState =="In Progress"){
	g_form.setValue('owner_approval',"Not Yet Completed");
	//g_form.setReadOnly('owner_approval',true);
}
if(myState =="Awaiting Approval"){
	g_form.setValue('owner_approval',"Awaiting Approval");
	g_form.setReadOnly('owner_approval',false);
	g_form.setReadOnly('survey_state',true);
}
   //Type appropriate comment here, and begin script below

}
