sys_script_client.script:
function onLoad() {
   var currentUser = g_user.userID;
   var productOwner = g_form.getValue('product_owner');
   var hasRole = g_user.hasRole('x_cama2_survey_dis.sd_admin');

   if(currentUser == productOwner || hasRole){
	   g_form.setReadOnly('assigned_to',false);
	   g_form.setReadOnly('survey_group', false);
	   //g_form.setReadOnly('assignment_group', false);
	    g_form.setReadOnly('distribution',false);
		g_form.setReadOnly('survey_assignee',false);
   }else{
	   g_form.setReadOnly('assigned_to',true);
	   g_form.setReadOnly('survey_group', true);
	   g_form.setReadOnly('assignment_group', true);
	   g_form.setReadOnly('product',true);
	   g_form.setReadOnly('distribution',true);
	   g_form.setReadOnly('survey_assignee',true);
   }

}
