sys_script_include.script:
var SurveyGroupCheck = Class.create();
SurveyGroupCheck.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

checkforsurveygroup: function() {
var userID = this.getParameter('sysparm_user_id');
var groupId = this.getParameter('sysparm_group');
var surveyId = this.getParameter('sysparm_survey_id');

var gr = new GlideRecord('sys_user_grmember');
gr.addQuery('user', userId);
gr.addQuery('group',groupId);
gr.query();

if(gr.next()){
	var assessment = new GlideRecord('asmt_assessment_instance');
	if(assessment.get(surveyId)){
		assessment.setValue('user', userId);
		assessment.update();
		return 'yes';
	}
}
return 'no';
},
    type: 'SurveyGroupCheck'
});
