sys_ui_action.script:
var currentUser = gs.getUserID();
var assignedTo = current.assigned_to;
var productOwner = current.product_owner;
var myRoles = gs.hasRole('x_cama2_survey_dis.sd_admin');



if(currentUser==assignedTo){
//gs.addInfoMessage("You do have permission to take this survey");//
takeSurvey();
//action.setRedirectURL(current);	
}else if(currentUser==productOwner){
gs.addInfoMessage("As the Product Owner, if you wish to adjust the survey please assign it to yourself.");
action.setRedirectURL(current);	
}else{
	gs.addErrorMessage("You do not have permission to take this survey, please contact the product owner.");
action.setRedirectURL(current);
}

