function takeSurvey(){	
	var type = 'ef25ece31bd031904570c91e1e4bcbf3';
	var url = 'assessment_take2.do?sysparm_assessable_sysid=' + current.active_survey.toString() + '&sysparm_assessable_type=' + type;
    action.setRedirectURL(url);
}
